import React from 'react';
import bridge from '@vkontakte/vk-bridge'; // VK Brige

import { 
    Panel,
    PanelHeader,
    PanelHeaderButton,
    Button,
    Group,
    Alert,
    Avatar,
    Placeholder,
    Separator,
    PullToRefresh,
    PanelSpinner,
    InfoRow,
    Header,
    Counter,
    SimpleCell,
    PromoBanner,
    FixedLayout,
    Cell,
    Div,
    HorizontalScroll,
    View,
    Switch,
    ScreenSpinner,
    ActionSheet,
    ActionSheetItem,
    Snackbar,
    ModalRoot,
    ModalCard,
    ModalPage,
    ModalPageHeader,
    Tabbar,
    TabbarItem,
    Epic,
    Input,
    FormLayout,
    List,
    Slider,
    ConfigProvider,
    platform
    } from '@vkontakte/vkui';

import Icon28RecentOutline from '@vkontakte/icons/dist/28/recent_outline';
import Icon24DoNotDisturb from '@vkontakte/icons/dist/24/do_not_disturb';
import Icon28CheckCircleOutline from '@vkontakte/icons/dist/28/check_circle_outline';
import Icon28InfoOutline from '@vkontakte/icons/dist/28/info_outline';
import Icon24BrowserBack from '@vkontakte/icons/dist/24/browser_back';
import Icon12Fire from '@vkontakte/icons/dist/12/fire';
import Icon16Done from '@vkontakte/icons/dist/16/done';

let month = "undefined"
function fix_time(time) {
    if(time < 10) {
        return "0" + time
    } else {
        return time
    }
  }
    class Reader extends React.Component {
        constructor(props) {
            super(props);
            this.state = {
                api_url: "https://xelene.ru/road/php/index.php?",
                other_profile: null
            }
            var props = this.props.this;
            this.setPopout = props.setPopout;
            this.showErrorAlert = props.showErrorAlert;
        }
        PrepareProfile(is_change=false){
            fetch(this.state.api_url + "method=user.getById&id=" + this.props.agent_id + "&" + window.location.search.replace('?', ''))
            .then(res => res.json())
            .then(data => {
              if(data.result) {

                this.setState({other_profile: data.response})
                if(is_change) {
                  // window.history.pushState({panel: 'other_profile'}, `other_profile`);
                  // this.setState({history: [...this.state.history, "other_profile"], activePanel: "other_profile"})
                //   this.props.this.goPanel("other_profile")
                }
              }
            })
            .catch(err => {
              this.showErrorAlert()

            })
        }
        copy(id, prometey) {
            this.setPopout(
              <ActionSheet onClose={() => this.setPopout(null)}>
                {this.state.is_special_moder ? 
                 <ActionSheetItem autoclose onClick={prometey ? () => this.closeProm(id) : () => this.giveProm(id)}>
                  {prometey ? 'Забрать прометей' : 'Выдать прометей'}
                </ActionSheetItem>
                : null }
                {this.state.is_special_moder ? 
                 <ActionSheetItem autoclose onClick={() => this.setActiveModal('ban_user')}>
                  Заблокировать
                </ActionSheetItem>
                : null }
                <ActionSheetItem autoclose onClick={() => bridge.send("VKWebAppCopyText", {text: "https://vk.com/app7409818#agent_id=" + id})}>
                  Скопировать ссылку
                </ActionSheetItem>
                {<ActionSheetItem autoclose theme="cancel">Отменить</ActionSheetItem>}
              </ActionSheet>)
          }

        please_Month(text) {
            let month = 'января'
            switch(text) {
               case 0:
                 month = "января"
               break;
               case 1:
                   month = "февраля"
               break;
               case 2:
                   month= "марта"
               break;
               case 3:
                   month= "апреля"
               break;
               case 4:
                   month= "мая"
               break;
               case 5:
                   month= "июня"
               break;
               case 6:
                   month= "июля"
               break;
               case 7:
                   month= "августа"
               break;
               case 8:
                   month= "сентября"
               break;
               case 9:
                   month= "октября"
               break;
               case 10:
                   month= "ноября"
               break;
               case 11:
                   month= "декабря"
               break;
            }
            return month
         }
        componentDidMount(){
            this.PrepareProfile()
        }

        render() {
            var props = this.props.this; // для более удобного использования.
            return (
                <Panel id={this.props.id}>
                <PanelHeader 
                    left={
                        <PanelHeaderButton onClick={() => window.history.back()}> 

                    <Icon24BrowserBack/>
                    </PanelHeaderButton>
                    }>
                        {/* <span onClick={() => props.copy(this.state.other_profile['id'], this.state.other_profile['flash'])}>Профиль</span> */}
                </PanelHeader>
                {console.log(this.state.other_profile)}
                {this.state.other_profile ? <>
                <Cell
                  description={
                      <div className="description_other_profile">
                     {this.state.other_profile['online']['is_online'] === true ? "online" : new Date(this.state.other_profile['online']['last_seen'] * 1e3).getDate() + " " + month + " "  + new Date(props.state.other_profile['online']['last_seen'] * 1e3).getFullYear() + " в " 
                  + fix_time(new Date(this.state.other_profile['online']['last_seen'] * 1e3).getHours()) + ":" + fix_time(new Date(this.state.other_profile['online']['last_seen'] * 1e3).getMinutes())}
                  </div>
                }
                  size="l"
                  before={<Avatar src={this.state.other_profile['avatar']['url']} size={70}/>}
                >
                  <div className="title_profile">
                  {isFinite(this.state.other_profile['nickname']) ? `Агент Поддержки #${this.state.other_profile['nickname']}` : this.state.other_profile['nickname'] ? this.state.other_profile['nickname'] : `Агент Поддержки #${props.state.other_profile['id']}`}
                    {this.state.other_profile['flash'] === true ? 
                    <div className="profile_moderator_name_icon">
                        <Icon12Fire onClick={() => props.setActiveModal('prom')}/>  
                    </div>
                    : null}
                    {this.state.other_profile['verified'] === true ?
                    <div className="profile_moderator_name_icon_ver">
                        <Icon16Done width={12} height={12}/>  
                    </div>
                    : null}
                  </div>
                  {this.please_Month(new Date(this.state.other_profile['registered'] * 1e3).getMonth() + 1)}
                  </Cell>
                    <Group>
                    <SimpleCell 
                        disabled
                        before={<Icon28CheckCircleOutline />}
                        indicator={new Date(this.state.other_profile.registered * 1e3).getDate() + " " + this.please_Month(new Date(this.state.other_profile['registered'] * 1e3).getMonth() + 1) + " " + new Date(this.state.other_profile.registered * 1e3).getFullYear()}>
                            Дата регистрации
                    </SimpleCell>
                    <SimpleCell 
                        disabled
                        before={<Icon28CheckCircleOutline />}
                        indicator={<Counter mode='primary'>{this.state.other_profile['total_answers']}</Counter>}>
                            Всего ответов
                    </SimpleCell>
                    <SimpleCell 
                        disabled
                        before={<Icon28CheckCircleOutline />}
                        indicator={<Counter mode='primary'>{this.state.other_profile['good_answers']}</Counter>}>
                            Положительные ответы
                    </SimpleCell>
                    <SimpleCell 
                        disabled
                        before={<Icon28CheckCircleOutline />}
                        indicator={<Counter mode='primary'>{this.state.other_profile['bad_answers']}</Counter>}>
                            Отрицательных ответов
                    </SimpleCell>
                </Group></> : null}

                {/* {props.state.other_profile != undefined ? this.please_Month(new Date(this.state.other_profile['registered'] * 1e3).getMonth() + 1) : null}
                <Cell
                  description={
                      <div className="description_other_profile">
                     {this.state.other_profile['online']['is_online'] === true ? "online" : new Date(this.state.other_profile['online']['last_seen'] * 1e3).getDate() + " " + month + " "  + new Date(props.state.other_profile['online']['last_seen'] * 1e3).getFullYear() + " в " 
                  + fix_time(new Date(this.state.other_profile['online']['last_seen'] * 1e3).getHours()) + ":" + fix_time(new Date(this.state.other_profile['online']['last_seen'] * 1e3).getMinutes())}
                  </div>
                }
                  size="l"
                  before={<Avatar src={this.state.other_profile['avatar']['url']} size={70}/>}
                >
                  <div className="title_profile">
                  {isFinite(this.state.other_profile['nickname']) ? `Агент Поддержки #${this.state.other_profile['nickname']}` : this.state.other_profile['nickname'] ? this.state.other_profile['nickname'] : `Агент Поддержки #${props.state.other_profile['id']}`}
                    {this.state.other_profile['flash'] === true ? 
                    <div className="profile_moderator_name_icon">
                        <Icon12Fire onClick={() => props.setActiveModal('prom')}/>  
                    </div>
                    : null}
                    {this.state.other_profile['verified'] === true ?
                    <div className="profile_moderator_name_icon_ver">
                        <Icon16Done width={12} height={12}/>  
                    </div>
                    : null}
                  </div>
                  {this.please_Month(new Date(this.state.other_profile['registered'] * 1e3).getMonth() + 1)}
                  </Cell>
                  {props.state.other_profile['nickname'] === 'Специальный Агент' || this.state.other_profile['banned'] ? <div style={{marginTop: "20px"}} className="help_title_profile">{this.state.other_profile['banned'] ? 'Этот профиль забанен' : 'Вы не можете просматривать этот профиль'}</div> :
                  <div>
                  <Separator></Separator>
                  <br/>
                  <Cell before={<Icon28RecentOutline height={30} width={30} className="other_icon"/>} className="information_profile">
                  <div className="minititle">
                      Дата регистрации
                  <div className="help_profile">{new Date(props.state.other_profile['registered'] * 1e3).getDate()} {month}  {new Date(this.state.other_profile['registered'] * 1e3).getFullYear()}</div>
                  </div>
                  </Cell>
                  <Cell before={<Icon28InfoOutline className="other_icon"/>} className="information_profile">
                  <div className="minititle">
                  Всего ответов
                  <div className="help_profile">{this.state.other_profile['total_answers']}</div>
                  </div>
                  </Cell>
                  <Cell before={<Icon28CheckCircleOutline className="other_icon"/>} className="information_profile">
                  <div className="minititle">
                  Положительных ответов
                  <div className="help_profile">{this.state.other_profile['good_answers']}</div>
                  </div>
                  </Cell>
                  <Cell before={<Icon24DoNotDisturb className="other_icon" height={28} width={28}/>} className="information_profile">
                  <div className="minititle">
                  Отрицательных ответов
                  <div className="help_profile">{this.state.other_profile['bad_answers']}</div>
                  </div>
                  </Cell>
