import React from 'react';
import bridge from '@vkontakte/vk-bridge'; // VK Brige

import { 
    Panel,
    PanelHeader,
    PanelHeaderButton,
    Button,
    Group,
    Alert,
    Avatar,
    Placeholder,
    Separator,
    PullToRefresh,
    PanelSpinner,
    InfoRow,
    Header,
    Counter,
    SimpleCell,
    PromoBanner,
    FixedLayout,
    Cell,
    Div,
    HorizontalScroll,
    View,
    Switch,
    ScreenSpinner,
    ActionSheet,
    ActionSheetItem,
    Snackbar,
    ModalRoot,
    ModalCard,
    ModalPage,
    ModalPageHeader,
    Tabbar,
    TabbarItem,
    Epic,
    Input,
    FormLayout,
    List,
    Slider,
    ConfigProvider,
    platform
    } from '@vkontakte/vkui';

import Icon24Up from '@vkontakte/icons/dist/24/up';
import Icon24BrowserBack from '@vkontakte/icons/dist/24/browser_back';
import Icon16ReplyOutline from '@vkontakte/icons/dist/16/reply_outline';

import Message from '../../../components/message'

const queryString = require('query-string');
const parsedHash = queryString.parse(window.location.search.replace('?', ''));

var months = [
    'января',
    'февраля',
    'марта',
    'апреля',
    'мая',
    'июня',
    'июля',
    'августа',
    'сентября',
    'ноября',
    'декабря',
];

function fix_time(time) {
    if(time < 10) {
        return "0" + time
    } else {
        return time
    }
}

function add_month(month) {
    let number_month = new Date(month * 1e3).getMonth()
    return months[number_month]
}

export default class Ticket extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            api_url: "https://xelene.ru/road/php/index.php?",
            tiket_info: [],
            tiket_message: [],
            tiket_send_message: '',
            add_comment: false,
            redaction: false,



        }
        var props = this.props.this;
        this.setPopout = props.setPopout;
        this.showErrorAlert = props.showErrorAlert;
        this.setActiveModal = props.setActiveModal;
        this.goOtherProfile = this.goOtherProfile.bind(this)


    }
    Prepare_ticket(){
        fetch(this.state.api_url + "method=ticket.getById&ticket_id=" + this.props.ticket_id + "&" + window.location.search.replace('?', ''))
        .then(res => res.json())
        .then(data => {
          if(data.result) {
            this.setState({tiket_info: data.response.info,
              tiket_message: data.response.messages });
          } else {
            this.props.this.setPopout( 
              <Alert
              actions={[{
                title: 'Отмена',
                autoclose: true,
                style: 'cancel'
              }]}
              onClose={() => this.props.this.setPopout(null)}
            >
              <h2>Ошибка</h2>
            <p>{data.error.message}</p>
            </Alert>
            )
          }
        })
        .catch(err => {
          this.showErrorAlert()

        })
    }
    sendRayt(mark, message_id) {
        let reyt = 1;
        if(mark === true) {
          reyt = 1;
        } else {
          reyt = 0;
        }
        if(reyt < 2) {
          fetch(this.state.api_url + "method=ticket.markMessage&message_id=" + message_id + "&mark=" + reyt + "&" + window.location.search.replace('?', ''))
            .then(res => res.json())
            .then(data => {
              if(data) {

                fetch(this.state.api_url + "method=ticket.getMessages&ticket_id=" + this.state.tiket_info['id'] + "&" + window.location.search.replace('?', ''))
          .then(res => res.json())
          .then(data => {
            if(data.response) {
              this.setState({tiket_message: data.response})
              this.setActiveModal(null);
              this.setPopout(null);
            }
          })
          .catch(err => {
            this.showErrorAlert()
          })
              }
            })
            .catch(err => {
                this.showErrorAlert()

            })
        }
      }
      goOtherProfile(id, is_change) {
        this.setState({popout: <ScreenSpinner/>})
        fetch(this.state.api_url + "method=user.getById&id=" + id + "&" + window.location.search.replace('?', ''))
          .then(res => res.json())
          .then(data => {
            if(data) {

              this.setState({other_profile: data.response, popout: null})
              if(is_change) {
                window.history.pushState({panel: 'other_profile'}, `other_profile`);
                this.setState({history: [...this.state.history, "other_profile"], activePanel: "other_profile"})
              }
            }
          })
          .catch(err => {
            this.showErrorAlert()

          })
      }
      Admin(id, author_id, text, comment, mark = -1){
          var idadm = id;
          console.log(id)
        this.props.this.setPopout(
          <ActionSheet onClose={() => this.setPopout(null)}>
            {author_id < 10000 ?
            <ActionSheetItem autoclose onClick={() => this.goOtherProfile(author_id, true)}>
              Профиль
            </ActionSheetItem>
            : null}
            { this.state.is_special_moder && mark !== 0 && mark !== 1 ? 
            <ActionSheetItem autoclose onClick={() => this.sendRayt(true, idadm)}>
              Оценить положительно
            </ActionSheetItem> 
            : null}
            { this.state.is_special_moder && mark !== 0 && mark !== 1 ? 
            <ActionSheetItem autoclose onClick={() => this.sendRayt(false, idadm)}>
              Оценить отрицательно
            </ActionSheetItem> 
            : null }
            { this.state.is_special_moder === true ? 
            <ActionSheetItem autoclose onClick={() => this.sendClear(idadm)}>
              Одобрить
            </ActionSheetItem> 
            : null }
            {/* { this.state.is_special_moder === true ? 
            comment === null || comment === undefined? 
            <ActionSheetItem autoclose onClick={() => this.setState({add_comment: true, message_id_add: idadm})}>
            Добавить комментарий
            </ActionSheetItem> 
            : null
            : null } */}
            {Number(author_id === this.state.test['id']) ? 
           <ActionSheetItem autoclose onClick={() => this.setState({redaction: true, message_id_redac: idadm, tiket_send_message: text})}>
           Редактировать
           </ActionSheetItem>
           : null
        }
        {comment === null || comment === undefined ? null : 
            <ActionSheetItem autoclose onClick={() => {this.props.this.setState({comment: comment}); this.props.setActiveModal("comment")}}>
            Просмотреть комментарий
          </ActionSheetItem>
            }
            {Number(author_id) === Number(this.state.test['id']) || this.state.is_special_moder === true ? 
            <ActionSheetItem autoclose onClick={() => this.deleteMessage(id)}>
              Удалить сообщение
            </ActionSheetItem>
            : null}
            {<ActionSheetItem autoclose theme="cancel">Отменить</ActionSheetItem>}
          </ActionSheet>)
      }
      copy(id) {
        this.setPopout(
          <ActionSheet onClose={() => this.setState({ popout: null })}>
            {Number(this.state.tiket_info['author']['id']) === Number(parsedHash.vk_user_id) ? 
            this.state.tiket_info['status'] === 0 ||  this.state.tiket_info['status'] === 1 ?
            <ActionSheetItem autoclose onClick={() => this.deleteTicket()}>
            Закрыть тикет
            </ActionSheetItem>
            : 
            <ActionSheetItem autoclose onClick={() => this.openTicket()}>
            Открыть тикет
            </ActionSheetItem>
            : 
            this.state.is_special_moder === true ?
            this.state.tiket_info['status'] === 0 ||  this.state.tiket_info['status'] === 1 ?
            <ActionSheetItem autoclose onClick={() => this.deleteTicket()}>
            Закрыть тикет
            </ActionSheetItem>
            : 
            <ActionSheetItem autoclose onClick={() => this.openTicket()}>
            Открыть тикет
            </ActionSheetItem>
            : null
          }
            <ActionSheetItem autoclose onClick={() => bridge.send("VKWebAppCopyText", {text: "https://vk.com/app7409818#ticket_id=" + id})}>
              Скопировать ссылку
            </ActionSheetItem>
            {<ActionSheetItem autoclose theme="cancel">Отменить</ActionSheetItem>}
          </ActionSheet>)
      }
      sendNewMessage() {
        this.setPopout(<ScreenSpinner/>)
        if(this.state.tiket_send_message.length >= 5) {
          var url = this.state.api_url + 'method=ticket.sendMessage&' + window.location.search.replace('?', '');
          var method = 'POST';
          var async = true;

          var xhr = new XMLHttpRequest();
          xhr.open( method, url, async );
          xhr.setRequestHeader("Content-type", "application/x-www-form-urlencoded");

          xhr.onreadystatechange = () => {
            if(xhr.status === 4) return;
            if ( xhr.status === 200 ) {
              fetch(this.state.api_url + "method=ticket.getMessages&ticket_id=" + this.state.tiket_info['id'] + "&" + window.location.search.replace('?', ''))
              .then(res => res.json())
              .then(data => {
                if(data) {
                  this.setState({tiket_message: data.response, tiket_send_message: "",add_comment: false, redaction: false, tiket_send_message: ""})
                  this.setPopout(null)
                }
              })
              .catch(err => {
                this.showErrorAlert()

              })
            }
          }

          xhr.send( 'ticket_id=' + this.state.tiket_info['id'] + '&text=' + this.state.tiket_send_message);

          xhr.onerror = ( error ) => {
            this.showErrorAlert()

            console.error( error );
          }
        } else {
            this.showErrorAlert("Текст сообщения должен быть минимум из 5 символов.")
        }
      }
      deleteMessage(message_id) {
        this.setState({popout: <ScreenSpinner/>})
          fetch(this.state.api_url + "method=ticket.deleteMessage&message_id=" + message_id + "&" + window.location.search.replace('?', ''))
            .then(res => res.json())
            .then(data => {
              if(data) {
                fetch(this.state.api_url + "method=ticket.getMessages&ticket_id=" + this.state.tiket_info['id'] + "&" + window.location.search.replace('?', ''))
          .then(res => res.json())
          .then(data => {
            if(data.response) {
              this.setState({tiket_message: data.response})
              this.setPopout(null)
            }
          })
          .catch(err => {
            this.showErrorAlert()

          })
              }
            })
            .catch(err => {
                this.showErrorAlert()

            })
      }
      sendNewMessageRedact() {
        this.setState({popout: <ScreenSpinner/>})
        if(this.state.tiket_send_message.length >= 5) {
          var url = this.state.api_url + 'method=ticket.editMessage&' + window.location.search.replace('?', '');
          var method = 'POST';
          var async = true;

          var xhr = new XMLHttpRequest();
          xhr.open( method, url, async );
          xhr.setRequestHeader("Content-type", "application/x-www-form-urlencoded");

          xhr.onreadystatechange = () => {
            if(xhr.status === 4) return;
            if ( xhr.status === 200 ) {
              fetch(this.state.api_url + "method=ticket.getMessages&ticket_id=" + this.state.tiket_info['id'] + "&" + window.location.search.replace('?', ''))
              .then(res => res.json())
              .then(data => {
                if(data) {
                  this.setState({tiket_message: data.response, tiket_send_message: "", add_comment: false, redaction: false, tiket_send_message: ""})
                  this.setPopout(null)
                }
              })
              .catch(err => {
                console.log(err)
              })
            }
          }

          xhr.send('message_id=' + this.state.message_id_redac + '&text=' + this.state.tiket_send_message);

          xhr.onerror = ( error ) => {
            this.showErrorAlert()

            console.error( error );
          }
        } else {
            this.showErrorAlert("Текст сообщения должен быть минимум из 5 символов.")
        }
      }

    componentDidMount(){
        this.Prepare_ticket()
    }
    render(){
        var props = this.props;
        var thisOb = this;
        return(
        <Panel id={this.props.id}>
            <PanelHeader 
                left={<PanelHeaderButton onClick={() => props.this.goBack()}><Icon24BrowserBack/></PanelHeaderButton>}
            >
                <span id="animation" onClick={() => this.copy(props.this.state.tiket_info['id'])}>Вопрос #{this.state.tiket_info['id']}</span>
            </PanelHeader>
            {/* MESSAGES */}
            <div className="title_tiket">{new Date(this.state.tiket_info['time'] * 1e3).getDate()} {add_month(this.state.tiket_info['time'])}</div>
            <div className="title_tiket" style={{marginTop: "10px", width: "95%", marginLeft: "10px"}}>Пользователь обратился с вопросом  «{this.state.tiket_info['title']}»</div>
                    {this.state.tiket_message ? this.state.tiket_message.map(function(result, i) {
                        var is_mine_ticket = Number(thisOb.state.tiket_info.author['id']) === Number(parsedHash.vk_user_id) ? true : false


                        var is_mine_message = Number(result['author']['id']) === Number(parsedHash.vk_user_id) ? true : false
                        var avatar = result.author.is_moderator ? result['author']['avatar']['url'] : result.author.photo_200
                        var time = fix_time(new Date(result.time * 1e3).getHours()) + ":" + fix_time(new Date(result.time * 1e3).getMinutes())
                        var title_moder = isFinite(result['nickname']) ? `Агент Поддержки #${result['nickname']}` : result['nickname'] ? result['nickname'] : `Агент Поддержки #${result['author']['id']}`;
                        return (
                            Number(thisOb.state.tiket_info.author['id']) === Number(parsedHash.vk_user_id) ?
                            <Message 
                                title={result.author.id === 526444378 ? "Витёк" : result.author.is_moderator ? title_moder : result.author.first_name + " " + result.author.last_name} 
                                title_icon={result.moderator_comment != undefined ? <Icon16ReplyOutline width={10} height={10} style={{display: "inline-block"}}/> : false}
                                is_mine={is_mine_message === is_mine_ticket}
                                avatar={avatar}
                                onClick={() => thisOb.Admin(result['id'], result['author']['id'], result['text'], result.moderator_comment != undefined ? result['moderator_comment']['text'] : null, result['mark'])}
                                key={i}
                                is_special={thisOb.state.is_special_moder}
                                is_mark={result.mark}
                                time={time}
                                sendRayt_false={() => thisOb.sendRayt(false, result.id)}
                                sendRayt_true={() => thisOb.sendRayt(true, result.id)}
                                approved={result.approved ? true : false}
                                CanselApp={() => props.this.showAlert('Информация', 'Этот ответ оценен отрицательно')}
                                DoneApp={() => props.this.showAlert('Информация', 'Этот ответ оценен положительно')}
                            >
                                {result.text}
                            </Message>

                            :
                            <Message 
                                title={result.author.id === 526444378 ? "Витёк" : result.author.is_moderator ? title_moder : result.author.first_name + " " + result.author.last_name} 
                                title_icon={result.moderator_comment != undefined ? <Icon16ReplyOutline width={10} height={10} style={{display: "inline-block"}}/> : false}
                                is_mine={result.author.is_moderator}
                                avatar={result.author.id === 526444378 ? "https://cdn3.iconfinder.com/data/icons/avatars-15/64/_Ninja-2-512.png" : avatar}
                                key={i}
                                time={time}
                                onClick={() => thisOb.Admin(result['id'], result['author']['id'], result['text'], result.moderator_comment != undefined ? result['moderator_comment']['text'] : null, result['mark'])}
                                is_special={props.this.state.is_special_moder}
                                is_mark={result.mark}
                                sendRayt_false={() => thisOb.sendRayt(false, result.id)}
                                sendRayt_true={() => thisOb.sendRayt(true, result.id)}
                                approved={result.approved ? true : false}
                                CanselApp={() => props.this.showAlert('Информация', 'Этот ответ оценен отрицательно')}
                                DoneApp={() => props.this.showAlert('Информация', 'Этот ответ оценен положительно')}
                            >
                                {result.text}
                            </Message>
                        )}) : null}
            {/* INPUT */}
            {this.state.tiket_info['status'] === 0 || (this.state.redaction === true || this.state.add_comment === true) ? 
                <div id="other" className="other">
                    <div className="down_input_message">
                        <textarea maxLength="2020" name="tiket_send_message" value={props.this.state.tiket_send_message} onChange={(e) => props.this.onChange(e)} placeholder={this.state.add_comment ? "Комментарий..." : "Ответ..."} className="textarea"></textarea>
                    </div>
                    <div className="send_text" onClick={() => this.state.redaction !== true && this.state.add_comment !== true ? this.sendNewMessage() : this.state.redaction ? this.sendNewMessageRedact() : this.sendNewMessageComment()}>
                        <Icon24Up/>
                    </div>
                </div> : null}
            {this.state.tiket_info['status'] === 1 ? 
                Number(props.this.state.tiket_info['author']['id']) === Number(parsedHash.vk_user_id) ?
                    <div className="others">
                        <Button size="l" style={{ marginRight: 8, width: "95%" }} onClick={() => this.deleteTicket()}>Проблема решена</Button><br/>
                        <div style={{height: "5px"}}></div>
                        <Button size="l" style={{ marginRight: 8, width: "95%" }} level="secondary" onClick={() => this.openTicket()}>Проблема не решена</Button>
                    </div>
                : null
            : null}
                <br/>
                <br/>
                <br/>
            </Panel>
        )
    }
};
