import React from 'react';

import { 
    Div,
    Panel,
    PanelHeader,
    Placeholder,
    } from '@vkontakte/vkui';
import Icon56DurationOutline from '@vkontakte/icons/dist/56/duration_outline';


export default class Banned extends React.Component {
        constructor(props) {
            super(props);
            this.state = {

            }
            var propsbi = this.props.this;
            this.setPopout = propsbi.setPopout;
            this.showErrorAlert = propsbi.showErrorAlert;
        }
        magic(el) {
            let tmp = document.createElement('p')
            tmp = el.cloneNode(true)
            tmp.style.width = el.offsetWidth + 'px'
            tmp.style.position = 'absolute'
            tmp.style.left = '-2000px'
            tmp.innerHTML = 'foo'
            document.body.appendChild(tmp)

            let content = el.textContent.split(''),
                oneLineHeight = tmp.scrollHeight,
                lines = [],
                i = 0

            while (i < content.length) {
              let line = tmp.innerHTML = ''
              while (i < content.length && tmp.scrollHeight <= oneLineHeight) {
                tmp.innerHTML = line += content[i++]
              }
              let lineEndIndex = i===content.length ? i : line.lastIndexOf(' ') + 1
              lines.push( content.splice(0, lineEndIndex).join('') )
              i = 0
            }
            tmp.remove()
            el.innerHTML = lines.map(line => '<span>'+line+'</span>').join('')

            //console.log(lines)
          }
        render() {
            return (
                <Panel id={this.props.id}> 
                <PanelHeader
                >
                Ban
                </PanelHeader>
                <Placeholder 
                icon={<Icon56DurationOutline/>}
                header='Ваш аккаунт был заблокирован'>
                  <p className='textban'>По причине:<br/>{this.props.reason}</p>
                </Placeholder>
            </Panel>
            )
            }
        }
