import React from 'react'; // React
import connect from '@vkontakte/vk-bridge'; // VK Connect
import vkQr from '@vkontakte/vk-qr';

import music from './music/Soloriver.mp3';

import { 
  Panel,
  PanelHeader,
  PanelHeaderButton,
  Button,
  Group,
  Alert,
  Avatar,
  Placeholder,
  Separator,
  PullToRefresh,
  PanelSpinner,
  InfoRow,
  Header,
  Counter,
  SimpleCell,
