import React from 'react';
import bridge from '@vkontakte/vk-bridge'; // VK Brige


import { 
    Panel,
    PanelHeader,
    Group,
    Alert,
    Counter,
    SimpleCell,
    Snackbar,
    PanelHeaderBack,
    CellButton,
    FormLayout,
    FormLayoutGroup,
    Input,
    Textarea,
    Checkbox,
    Button,
    } from '@vkontakte/vkui';

import Icon28SmartphoneOutline from '@vkontakte/icons/dist/28/smartphone_outline';

export default class Settings extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            api_url: "https://xelene.ru/road/php/index.php?",

        }
        var propsbi = this.props.this;
        this.setPopout = propsbi.setPopout;
        this.showErrorAlert = propsbi.showErrorAlert;
        this.showAlert = propsbi.showAlert;
        this.setActiveModal = propsbi.setActiveModal;
        this.onChange = (event) => {
            var name = event.currentTarget.name;
            var value = event.currentTarget.value;
            this.setState({ [name]: value });
        }
    }
    deleteAccount(){
      fetch(this.state.api_url + "method=account.delete&" + window.location.search.replace('?', ''))
      .then(res => res.json())
      .then(data => {
      if(data.result) {
        bridge.send("VKWebAppClose", {"status": "success"});
      }else{
          this.showErrorAlert(data.error.message)
      }
      })
      .catch(err => {
          this.showErrorAlert(err)

      })
    }

    render() {
        var props = this.props.this;
        return(
            <Panel id={this.props.id}>
                <PanelHeader 
                    left={
                        <PanelHeaderBack onClick={() => this.props.this.goBack()} /> 
                }>
                    Верификация
                </PanelHeader>
                <FormLayout>
                  <FormLayoutGroup top="Общая информация" >
                    <Input placeholder='Введите свой текст...' />
                  </FormLayoutGroup>
                  <FormLayoutGroup top="Почему вы решили верефицировать карточку">
                    <Textarea placeholder='Введите свой текст...' />
                  </FormLayoutGroup>
                  <SimpleCell
                  description="Нажмите чтобы указать номер телефона"
                  before={<Icon28SmartphoneOutline/>}>Ваш номер телефона</SimpleCell>
                  <Checkbox>Согласен с правилами верификации</Checkbox>
                  <Checkbox>Готов к телефонному разговору с модератором отдела верификации</Checkbox>
                </FormLayout>
                <Div>
                  <Button size=''>Отправить на рассмотрение</Button>
                </Div>
                {this.props.this.state.snackbar}
            </Panel>
        )
    }
}
