import React from 'react';
import bridge from '@vkontakte/vk-bridge'; // VK Brige


import { 
    Panel,
    PanelHeader,
    Group,
    Alert,
    Counter,
    SimpleCell,
    Snackbar,
    PanelHeaderBack,
    CellButton,
    Div,
    FormLayout,
    FormLayoutGroup,
    Input,
    Textarea,
    Checkbox,
    Button,
    PanelHeaderButton,

    } from '@vkontakte/vkui';

import Icon28SmartphoneOutline from '@vkontakte/icons/dist/28/smartphone_outline';

const platformname = (/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent));


export default class Verfy extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            api_url: "https://xelene.ru/road/php/index.php?",
            check1: false,
            check2: false,
            title: '',
            description: '',
            number: '',

        }
        var propsbi = this.props.this;
        this.setPopout = propsbi.setPopout;
        this.showErrorAlert = propsbi.showErrorAlert;
        this.showAlert = propsbi.showAlert;
        this.setActiveModal = propsbi.setActiveModal;
        this.onChange = (event) => {
            var name = event.currentTarget.name;
            var value = event.currentTarget.value;
            this.setState({ [name]: value });
        }
    }
    han(){
      fetch(this.state.api_url + "method=account.delete&" + window.location.search.replace('?', ''))
      .then(res => res.json())
      .then(data => {
      if(data.result) {
        bridge.send("VKWebAppClose", {"status": "success"});
      }else{
          this.showErrorAlert(data.error.message)
      }
      })
      .catch(err => {
          this.showErrorAlert(err)

      })
    }

    render() {
        var props = this.props.this;
        return(
            <Panel id={this.props.id}>
                <PanelHeader 
                    left={<>
                    <PanelHeaderBack onClick={() => this.props.this.goBack()}></PanelHeaderBack>
                    {platformname ? null : <PanelHeaderButton onClick={() => this.Prepare_top(true)}></PanelHeaderButton>}</>}>
                    Верификация
                </PanelHeader>
                <FormLayout>
                  <FormLayoutGroup top="Общая информация" >
                    <Input 
                    onChange={this.onChange}
                    name='title'
                    placeholder='Введите свой текст...' />
                  </FormLayoutGroup>
                  <FormLayoutGroup top="Почему вы решили верефицировать профиль">
                    <Textarea 
                    name='description'
                    onChange={this.onChange}
                    placeholder='Введите свой текст...' />
                  </FormLayoutGroup>
                  <SimpleCell
                  description="Нажмите чтобы указать номер телефона"
                  before={<Icon28SmartphoneOutline/>}>Ваш номер телефона</SimpleCell>
                  <Checkbox checked={this.state.check1} onChange={() => this.state.check1 ? this.setState({check1: false}) : this.setState({check1: true})}>Согласен с правилами верификации</Checkbox>
                  <Checkbox checked={this.state.check2} onChange={() => this.state.check2 ? this.setState({check2: false}) : this.setState({check2: true})}>Готов к телефонному разговору с модератором отдела верификации</Checkbox>
                </FormLayout>
                <Div>
                  <Button size='xl' stretched>Отправить на рассмотрение</Button>
                </Div>
                {this.props.this.state.snackbar}
            </Panel>
        )
    }
}
