import React from 'react'; // React
import bridge from '@vkontakte/vk-bridge'; // VK Brige
import vkQr from '@vkontakte/vk-qr';


import { 
  Button,
  Alert,
  Avatar,
  Header,
  Cell,
  Div,
  View,
  ScreenSpinner,
  ModalRoot,
  ModalCard,
  ModalPage,
  ModalPageHeader,
  Input,
  List,
  } from '@vkontakte/vkui';

import '@vkontakte/vkui/dist/vkui.css';
import '../../style.css';
// Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚Ð¸Ñ€ÑƒÐµÐ¼ Ð¿Ð°Ð½ÐµÐ»Ð¸
import Prof from './panels/profile';
import Market from './panels/market';
import Achievements from './panels/achives';
import MYQuest from './panels/AllQuestions';
import Settings from './panels/settings';
import SchemeChange from './panels/schemechange';
import Info from './panels/info'
import Verfy from './panels/verfy'
import Tiket from '../../components/tiket';
import OtherProfile from '../../components/other_profile'

//Ð˜Ð¼Ð¿Ð¾Ñ€Ñ‚Ð¸Ñ€ÑƒÐµÐ¼ Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ñ‹Ðµ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ¸
import ModalPrometay from '../../Modals/Prometay';
import ModalDonut from '../../Modals/Donut'

import Icon24Dismiss from '@vkontakte/icons/dist/24/dismiss';
import Icon24Qr from '@vkontakte/icons/dist/24/qr';
import Icon28MessagesOutline from '@vkontakte/icons/dist/28/messages_outline';
import Icon24Linked from '@vkontakte/icons/dist/24/linked';
import Icon56MoneyTransferOutline from '@vkontakte/icons/dist/56/money_transfer_outline'
import Icon20PlaceOutline from '@vkontakte/icons/dist/20/place_outline';
import Icon20Stars from '@vkontakte/icons/dist/20/stars';


// const queryString = require('query-string');
// const platformname = (/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent));
// const parsedHash = queryString.parse(window.location.search.replace('?', ''));
// const hash = queryString.parse(window.location.hash);
function qr(agent_id, sheme) {
  let hex = "foregroundColor"
  if(sheme === "bright_light") {
    hex = "#000"
  }
  if(sheme === "space_gray") {
    hex = "#fff"
  }
  return (
    vkQr.createQR('https://vk.com/app7409818#agent_id=' + agent_id, {
    qrSize: 120,
    isShowLogo: true,
    foregroundColor: hex
  })
  )
}
export default class Main extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            api_url: "https://xelene.ru/road/php/index.php?",
            activePanel: 'profile',
            activeModal: null,
            modalHistory: [],
            popout: this.props.popout,
            ticket_id: null,
            history: ['profile'],
            active_other_profile: 0,
            other_profile: null,
            ban_reason: "",
            comment: '',
            transfer: {
              'avatar': '',
              'comment': ''
            },
            moneys: null,
            money_transfer_send: null,
            money_transfer_count: null,
            AgeUser: 0,
            snackbar: null,
        }
        this.changeData = this.props.this.changeData;
        this.playAudio = this.props.this.playAudio;
        this.ReloadProfile = this.props.reloadProfile;
        this.ChangeData = this.props.this.changeData;
        this.setPopout = (value) => {
          this.setState({popout: value})
        }
        this.setSnack = (value) => {
          this.setState({snackbar: value})
        }
        this.goTiket = (id) => {
          this.setPopout(<ScreenSpinner/>)
          this.setState({ticket_id: id})
          this.goPanel('ticket');
          this.setPopout(null);
        }
        this.onChange = (event) => {
          var name = event.currentTarget.name;
          var value = event.currentTarget.value;
          this.setState({ [name]: value });
      }
        this.goOtherProfile = (id) => {
          this.setState({active_other_profile: id})
          this.goPanel("other_profile")
        }
        this.modalBack = () => {
            this.setActiveModal(this.state.modalHistory[this.state.modalHistory.length - 2]);
        };
        this.goBack = () => {
          const history = this.state.history;
          if(history.length === 1) {
              bridge.send("VKWebAppClose", {"status": "success"});
          } else if (history.length > 1) {
              history.pop()
              this.setState({activePanel: history[history.length - 1], snackbar: null})
              // if(history[history.length - 1] === 'ticket'){
              //   this.changeData('need_epic', false)
              // } else{
              //   this.changeData('need_epic', true)
              // }
          }
      }
        this.goPanel = (panel) => {
          this.setState({history: [...this.state.history, panel], activePanel: panel, snackbar: null})
          // if(panel === 'ticket'){
          //   this.changeData('need_epic', false)
          // } else{
          //   this.changeData('need_epic', true)
          // }
        }
        this.setActiveModal = (activeModal) => {
            activeModal = activeModal || null;
            let modalHistory = this.state.modalHistory ? [...this.state.modalHistory] : [];
        
            if (activeModal === null) {
              modalHistory = [];
            } else if (modalHistory.indexOf(activeModal) !== -1) {
              modalHistory = modalHistory.splice(0, modalHistory.indexOf(activeModal) + 1);
            } else {
              modalHistory.push(activeModal);
            }
        
            this.setState({
              activeModal: activeModal,
              modalHistory: modalHistory
            });
          };
          this.showAlert = (title, text) => {
            this.setState({
              popout: 
                <Alert
                  actions={[{
                    title: 'Ð—Ð°ÐºÑ€Ñ‹Ñ‚ÑŒ',
                    autoclose: true,
                    mode: 'cancel'
                  }]}
                  onClose={() => this.setPopout(null)}
                >
                  <h2>{title}</h2>
                  <p>{text}</p>
              </Alert>
            })
          }
          this.showErrorAlert = (error=null) => {
            this.setPopout(
              <Alert
                  actions={[{
                  title: 'ÐžÑ‚Ð¼ÐµÐ½Ð°',
                  autoclose: true,
                  mode: 'cancel'
                  }]}
                  onClose={() => this.setPopout(null)}
              >
                <h2>ÐžÑˆÐ¸Ð±ÐºÐ°</h2>
                {error ? <p>{error}</p> : <p>Ð§Ñ‚Ð¾-Ñ‚Ð¾ Ð¿Ð¾ÑˆÐ»Ð¾ Ð½Ðµ Ñ‚Ð°Ðº, Ð¿Ð¾Ð¿Ñ€Ð¾Ð±ÑƒÐ¹Ñ‚Ðµ ÑÐ½Ð¾Ð²Ð°!</p>}
              </Alert>
          )
        }
    }
    ChangeAge(age) {
      this.setState({popout: <ScreenSpinner/>})
      fetch(this.state.api_url + "method=account.setAge&age=" + age + "&" + window.location.search.replace('?', ''))
      .then(res => res.json())
      .then(data => {
        if(data.result) {
          this.setState({popout: null})
          // setTimeout(() => {
          //   this.playAudio()
          // }, 5000)
          
        }
      })
      .catch(err => {
        this.showErrorAlert(err)
      })
    }
    userBan(user_id, text) {
      this.setPopout(<ScreenSpinner/>)
      fetch(this.state.api_url + "method=account.ban&agent_id=" + user_id + "&banned=true&reason=" + text + "&" + window.location.search.replace('?', ''))
      .then(res => res.json())
      .then(data => {
        if(data.result) {
          this.setActiveModal(null);
          this.showAlert('Ð£ÑÐ¿ÐµÑ…', 'ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ Ð·Ð°Ð±Ð°Ð½ÐµÐ½');
          this.setPopout(null);
        }else {
          this.showErrorAlert(data.error.message)
        }
      })
      .catch(err => {
        this.showErrorAlert(err)
      })
    }
    sendMoney() {
      fetch(this.state.api_url + 'method=transfers.send&summa=' +  this.state.money_transfer_count + '&send_to=' + this.state.money_transfer_send + "&" + window.location.search.replace('?', ''))
      .then(data => data.json())
      .then(data => {
        if(data.result) {
          this.setState({moneys: data.response})
          this.setActiveModal("moneys")
        } else {
          this.showErrorAlert(data.error.message)
        }
      })
    }
    render() {
        const modal = (
            <ModalRoot
            activeModal={this.state.activeModal}
            >
              <ModalPage
                id="settings"
                onClose={this.modalBack}
                header={
                  <ModalPageHeader
                  right={<Header onClick={this.modalBack}><Icon24Dismiss style={{color: 'var(--placeholder_icon_foreground_primary)'}} /></Header>}
                  >
                    ÐÐ°ÑÑ‚Ñ€Ð¾Ð¹ÐºÐ¸
                  </ModalPageHeader>
                }
                >
                  <Div style={{display:'flex'}}>
                    <Button onClick={() => {
                      this.setActiveModal(null);
                      this.getRandomTiket();

                    }} 
                    stretched 
                    size='l'
                    // style={{marginRight: '2%'}}
                    before={<Icon20PlaceOutline />}>Ð¡Ð»ÑƒÑ‡Ð°Ð¹Ð½Ñ‹Ð¹ Ñ‚Ð¸ÐºÐµÑ‚</Button>
                    <Button onClick={() => {
                      this.setActiveModal(null);
                      this.props.this.changeData('scheme',(this.props.this.state.scheme === 'bright_light') ? 'space_gray' : 'bright_light')
                    }}
                    stretched 
                    size='l'
                    style={{marginLeft: '5%'}}
                    before={<Icon20Stars />}>Ð¡Ð¼ÐµÐ½Ð¸Ñ‚ÑŒ Ñ‚ÐµÐ¼Ñƒ</Button>
                  </Div>
                </ModalPage>

              <ModalPrometay
              id='prom'
              onClose={() => this.setActiveModal(null)}
              action={() => this.setActiveModal(null)} />

              <ModalDonut
              id='donut'
              onClose={() => this.setActiveModal(null)}
              action={() => this.setActiveModal(null)} />

              <ModalCard
                id='ban_user'
                onClose={() => this.setActiveModal(null)}
                icon={<Avatar src={this.state.other_profile ? this.state.other_profile['avatar']['url'] : null} size={72} />}
                header="Ð—Ð°Ð±Ð°Ð½Ð¸Ñ‚ÑŒ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ"
                actions={[{
                  title: 'Ð—Ð°Ð±Ð°Ð½Ð¸Ñ‚ÑŒ! ðŸ¤¬',
                  mode: 'secondary',
                  action: () => {
                    this.userBan(this.state.other_profile ? this.state.other_profile['id'] : 0, this.state.ban_reason);
                  }
                }]}
              >
                <Input disabled value={this.state.other_profile ? (this.state.other_profile['id'] < 0) ? -this.state.other_profile['id'] : this.state.other_profile['id'] : null}/>
                <br/>
                <Input maxLength="100" name="ban_reason" onChange={(e) => this.onChange(e)} placeholder="Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ Ð¿Ñ€Ð¸Ñ‡Ð¸Ð½Ñƒ Ð±Ð°Ð½Ð°" value={this.state.ban_reason} />
                
              </ModalCard>
              <ModalCard
                id='send'
                onClose={() => this.setActiveModal(null)}
                icon={<Icon56MoneyTransferOutline />}
                header="ÐžÑ‚Ð¿Ñ€Ð°Ð²Ð»ÑÐ¹Ñ‚Ðµ Ð¼Ð¾Ð½ÐµÑ‚ÐºÐ¸ Ð´Ñ€ÑƒÐ·ÑŒÑÐ¼"
                actions={[{
                  title: 'ÐžÑ‚Ð¿Ñ€Ð°Ð²Ð¸Ñ‚ÑŒ',
                  mode: 'secondary',
                  disabled: true,
                  action: () => {
                    this.sendMoney();
                  }
                }]}
              >
                <Input maxLength="5" onChange={(e) => this.onChange(e)} placeholder="Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ id Ð°Ð³ÐµÐ½Ñ‚Ð°" name="money_transfer_send" value={this.state.money_transfer_send}/>
                <br/>
                <Input maxLength="5" name="money_transfer_count" onChange={(e) => this.onChange(e)} placeholder="Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ ÐºÐ¾Ð»-Ð²Ð¾ Ð¼Ð¾Ð½ÐµÑ‚Ð¾Ðº" value={this.state.money_transfer_count} />
                {/* <br/>
                <Input maxLength="100" name="money_transfer_comment" onChange={(e) => this.onChange(e)} placeholder="Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ ÐºÐ¾Ð¼Ð¼ÐµÐ½Ñ‚Ð°Ñ€Ð¸Ð¹ Ðº Ð¿ÐµÑ€ÐµÐ²Ð¾Ð´Ñƒ" value={this.state.money_transfer_comment} /> */}
                
              </ModalCard>
              <ModalCard
                id='moneys'
                onClose={() => this.setActiveModal(null)}
                icon={<Avatar src={this.state.moneys ? this.state.moneys.avatar : null} size={72} />}
                header={this.state.moneys ? "Ð’Ð°Ñˆ Ð±Ð°Ð»Ð°Ð½Ñ: " + this.state.moneys.money : null}
                caption={this.state.moneys ? this.state.moneys.text : null}
                actions={[{
                  title: 'Ð—Ð°ÐºÑ€Ñ‹Ñ‚ÑŒ',
                  mode: 'secondary',
                  action: () => {
                    this.setActiveModal(null);
                    this.setState({moneys: null, money_transfer_count: null, money_transfer_send: null})
                  }
                }]}
              >
              </ModalCard>
              <ModalCard
                id='transfer'
                onClose={() => this.setActiveModal(null)}
                icon={<Avatar src={this.state.transfer.avatar} size={72} />}
                header='ÐŸÐµÑ€ÐµÐ²Ð¾Ð´ Ð¼Ð¾Ð½ÐµÑ‚Ð¾Ðº'
                caption={this.state.transfer.comment}
                actions={[{
                  title: 'Ð—Ð°ÐºÑ€Ñ‹Ñ‚ÑŒ',
                  mode: 'secondary',
                  action: () => {
                    this.setActiveModal(null);
                  }
                }]}
              >
              </ModalCard>
              <ModalPage
                id="share"
                onClose={this.modalBack}
                header={
                  <ModalPageHeader
                  right={<Header onClick={this.modalBack}><Icon24Dismiss /></Header>}
                  >
                    ÐŸÐ¾Ð´ÐµÐ»Ð¸Ñ‚ÑŒÑÑ
                  </ModalPageHeader>
                }
                >
                  <List>
                    <Cell onClick={() => this.setActiveModal("qr")} before={<Icon24Qr width={28} height={28}/>}>QR-code</Cell>
                    <Cell onClick={() => {bridge.send("VKWebAppShare", {"link": "https://vk.com/app7409818#agent_id=" + this.props.account['id']}); this.setActiveModal(null)}} before={<Icon28MessagesOutline width={28} height={28}/>}>Ð’ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ</Cell>
                    <Cell onClick={() => {bridge.send("VKWebAppCopyText", {text: "https://vk.com/app7409818#agent_id=" + this.props.account['id']}); this.setActiveModal(null)}} before={<Icon24Linked width={28} height={28}/>}>Ð¡ÐºÐ¾Ð¿Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ ÑÑÑ‹Ð»ÐºÑƒ</Cell>
                  </List>
                </ModalPage>
                <ModalPage
                id="qr"
                onClose={this.modalBack}
                header={
                  <ModalPageHeader
                  right={<Header onClick={this.modalBack}><Icon24Dismiss style={{color: 'var(--placeholder_icon_foreground_primary)'}} /></Header>}
                  >
                    QR
                  </ModalPageHeader>
                }
                >
                 {<div className="qr" dangerouslySetInnerHTML={{__html: qr(this.props.account['id'], this.props.this.state.scheme)}}/>}
                 <br/>
                 <div className="qr">ÐžÑ‚ÑÐºÐ°Ð½Ð¸Ñ€ÑƒÐ¹Ñ‚Ðµ ÐºÐ°Ð¼ÐµÑ€Ð¾Ð¹ Ð’ÐšÐ¾Ð½Ñ‚Ð°ÐºÑ‚Ðµ!</div>
                 <br/>
                </ModalPage>
            </ModalRoot>
        )
        return(
            <View 
            id={this.props.id}
            activePanel={this.state.activePanel}
            modal={modal}
            popout={this.state.popout}
            onSwipeBack={this.goBack}
            >

              <Prof id="profile" this={this} account={this.props.account} />
              <MYQuest id="qu" this={this} account={this.props.account} /> 
              <Market id="market" this={this} account={this.props.account} />
              <Achievements id="achievements" this={this} account={this.props.account} />
              <Settings id="settings" this={this} account={this.props.account} />
              <SchemeChange id="schemechange" this={this} account={this.props.account} />
              <Info id='info' this={this} />
              <Verfy id='info' this={this} />
              <Tiket id="ticket" this={this} ticket_id={this.state.ticket_id} account={this.props.account} />
              <OtherProfile id="other_profile" this={this} agent_id={this.state.active_other_profile} account={this.props.account}/>
            </View>   
        )
    }
}
