import React from 'react';

import { 
    Panel,
    PanelHeader,
    Group,
    Avatar,
    Button,
    Alert,
    Placeholder,
    Separator,
    PullToRefresh,
    PanelSpinner,
    InfoRow,
    Header,
    Counter,
    SimpleCell,
    PromoBanner,
    FixedLayout,
    Cell,
    Div,
    HorizontalScroll,
    View,
    Switch,
    } from '@vkontakte/vkui';

import Icon28FireOutline from '@vkontakte/icons/dist/28/fire_outline';
import Icon28CheckCircleOutline from '@vkontakte/icons/dist/28/check_circle_outline';
import Icon24DoNotDisturb from '@vkontakte/icons/dist/24/do_not_disturb';
import Icon16Chevron from '@vkontakte/icons/dist/16/chevron';
import Icon12Fire from '@vkontakte/icons/dist/12/fire';
import Icon16Verified from '@vkontakte/icons/dist/16/verified';
import Icon24ShareOutline from '@vkontakte/icons/dist/24/share_outline';
import Icon28GhostOutline from '@vkontakte/icons/dist/28/ghost_outline';
import Icon28PollSquareOutline from '@vkontakte/icons/dist/28/poll_square_outline';
import Icon28MarketOutline from '@vkontakte/icons/dist/28/market_outline';
import Icon28ArticleOutline from '@vkontakte/icons/dist/28/article_outline';
import Icon28Notifications from '@vkontakte/icons/dist/28/notifications';
import Icon12Verified from '@vkontakte/icons/dist/12/verified';


export default class Profile extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            api_url: "https://xelene.ru/road/php/index.php?",

        }
    }
    render(){
        var props = this.props.this;
        console.log(this.props.account)
        return(
            <Panel id={this.props.id}>
                <PanelHeader>Профиль</PanelHeader>
                    {/* <div className="profile">
                        <div className="image_profile">
                            <img className="image_profile" src={this.props.account['avatar']['url']}/>
                        </div>
                        <div className="profiles">
                            <div className="name_profile">  
                        {isFinite(this.props.account['nickname']) ? `Агент Поддержки` : this.props.account['nickname'] ? this.props.account['nickname'] : `Агент Поддержки`}
                                {
                                this.props.account['flash'] === true ? 
                                    <div className="profile_icon">
                                        <Icon12Fire onClick={() => props.setActiveModal('prom')}/>  
                                    </div>
                                    : null}
                                {this.props.account['verified'] === true ? 
                                    <div className="profile_icon_ver">
                                        <Icon16Verified />  
                                    </div>
                                    : null}
                            </div>
                            <div className="description_profile">
                        {isFinite(this.props.account['nickname']) ? `#${this.props.account['nickname']}` : this.props.account['nickname'] ? '' : `#${this.props.account['id']}`}
                            </div>
                        </div>
                    </div> */}
                    <SimpleCell
                    before={<Avatar src={this.props.account['avatar']['url']} size={72} />}
                    size="l"
                    description="#132"
                    // asideContent={<Icon24MoreHorizontal />}
                    // bottomContent={
                    //   <div style={{ display: 'flex' }}>
                    //     <Button size="m">Добавить</Button>
                    //     <Button size="m" mode="secondary" style={{ marginLeft: 8 }}>Скрыть</Button>
                    //   </div>
                    // }
                    >
                        <div style={{display:"flex"}}>
                            <p style={{margin:0}}>Фгкнт поддержки</p> 
                            {
                                this.props.account['flash'] === true ? 
                                    <div className="profile_icon">
                                        <Icon12Fire w onClick={() => props.setActiveModal('prom')}/>  
                                    </div>
                                    : null}
                                {this.props.account['verified'] === true ? 
                                    <div className="profile_icon_ver">
                                        <Icon16Verified />  
                                    </div>
                                    : null}
                        </div>

                    </SimpleCell>
                    <Separator />
                    <Div>
                        <Button
                        stretched
                        size='l'
                        mode='secondary'
                        >Настройки</Button>
                    </Div>

                    <Separator />
                    <Group>
                        <SimpleCell
                            expandable
                            href="https://vk.me/join/AJQ1dxm7YRd7rN/MQHaAtZx7"
                            target="_blank" rel="noopener noreferrer"
                            before={<Icon28GhostOutline />}>
                                Фантом-чат
                        </SimpleCell>

                        <SimpleCell 
                        disabled
                        before={<Icon28CheckCircleOutline />}
                        indicator={<Counter mode='primary'>{this.props.account['good_answers']}</Counter>}>Положительные ответы</SimpleCell>

                        <SimpleCell 
                        disabled
                        before={<Icon24DoNotDisturb
                        width={28} 
                        height={28} />}
                        indicator={<Counter mode='primary'>{this.props.account['bad_answers']}</Counter>}>Отрицательные ответы</SimpleCell>
                    </Group>
                    <Group>
                        <SimpleCell
                        expandable
                        indicator={<Counter mode='primary'>{this.props.account['total_answers']}</Counter>}
                        onClick={() => {
                            props.this.goPanel('qu');
                        }}
                        before={<Icon28PollSquareOutline />}>Мои ответы</SimpleCell>
                        <SimpleCell
                        expandable
                        onClick={() => {
                            props.this.goMoney();
                        }}
                        before={<Icon28MarketOutline />}>Магазин</SimpleCell>
                        {/* props.this.state.profile['balance'] Вынести отдельно в магазин */}

                        <SimpleCell
                        expandable
                        onClick={() => {
                            props.this.goAchive();
                        }}
                        before={<Icon28FireOutline />}>Достижения</SimpleCell>
                        {/* <SimpleCell
                        expandable
                        onClick={() => {
                            props.this.Dop();
                        }}
                        before={<Icon28ArticleOutline />}>Мои вопросы</SimpleCell> */}

                        <SimpleCell
                        expandable
                        onClick={() => {
                            props.this.setActiveModal("share");
                        }}
                        before={<Icon24ShareOutline />}>Поделиться профилем</SimpleCell>

                    </Group>
                    {/* <Group>
                        <SimpleCell
                        onClick={() => {
                            props.this.allowMessage();
                        }}
                        indicator={<Switch onChange={() => props.this.allowMessage()} defaultChecked={props.this.state.switchKeys}/>}
                        before={<Icon28Notifications />}>Уведомления в сообщения</SimpleCell>
                    </Group> */}
                {this.props.this.state.snackbar}
            </Panel>
        )
    }

}
