import React from 'react';
import { 
    Panel,
    PanelHeader,
    PanelHeaderButton,
    ScreenSpinner,

    Group,
    Input,
    Avatar,
    Button,
    Alert,
    Placeholder,
    Separator,
    PullToRefresh,
    PanelSpinner,
    InfoRow,
    Header,
    Counter,
    SimpleCell,
    PromoBanner,
    FixedLayout,
    Cell,
    Div,
    HorizontalScroll,
    View,
    Switch,
    Snackbar,
    PanelHeaderBack,
    CellButton,
    Radio,
    FormLayout,
    } from '@vkontakte/vkui';

import Icon28DoneOutline from '@vkontakte/icons/dist/28/done_outline';
import Icon28CoinsOutline from '@vkontakte/icons/dist/28/coins_outline';
import Icon28DeleteOutline from '@vkontakte/icons/dist/28/delete_outline';
import Icon28PaletteOutline from '@vkontakte/icons/dist/28/palette_outline';
import Icon28TargetOutline from '@vkontakte/icons/dist/28/target_outline';


export default class SchemeChange extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            api_url: "https://xelene.ru/road/php/index.php?",

        }
        var props = this.props.this;
        this.setPopout = props.setPopout;
        this.showErrorAlert = props.showErrorAlert;
        this.showAlert = props.showAlert;
        this.setActiveModal = props.setActiveModal;
        this.onChange = (event) => {
            var name = event.currentTarget.name;
            var value = event.currentTarget.value;
            this.setState({ [name]: value });
        }
    }
    ChangeScheme(e){
        this.set
        let value = e.currentTarget.value;
        fetch(this.props.this.state.api_url + "method=account.changeScheme&scheme=" + value + "&" + window.location.search.replace('?', ''))
            .then(res => res.json())
            .then(data => {
            if(data.result) {
                console.log(data.response)
                this.setState({myQuestions: data.response})
                setTimeout(() => {
                    this.setState({fetching: false});
            }, 500)

            }
            })
            .catch(err => {
            this.props.this.showErrorAlert()

            })

    }

    render() {
        var props = this.props.this;
        return(
            <Panel id={this.props.id}>
                <PanelHeader 
                    left={
                        <PanelHeaderBack onClick={() => this.props.this.goBack()} /> 
                }>
                    Смена темы
                </PanelHeader>
                <FormLayout>
                    <div>
                    <Radio name="radio" onChange={this.ChangeScheme} value="1" defaultChecked>Светлая тема</Radio>
                    <Radio name="radio" onChange={this.ChangeScheme} value="2">Тёмная тема</Radio>
                    <Radio name="radio" onChange={this.ChangeScheme} value="0" description='Сервис будет использовать тему установленную в настройках вк'>Автоматически</Radio>
                    </div>
                </FormLayout>
            </Panel>
        )
    }
}
