import React from 'react';
import { 
    Panel,
    PanelHeader,
    PanelHeaderButton,
    ScreenSpinner,

    Group,
    Input,
    Avatar,
    Button,
    Alert,
    Placeholder,
    Separator,
    PullToRefresh,
    PanelSpinner,
    InfoRow,
    Header,
    Counter,
    SimpleCell,
    PromoBanner,
    FixedLayout,
    Cell,
    Div,
    HorizontalScroll,
    View,
    Switch,
    Snackbar,
    PanelHeaderBack,
    CellButton,
    } from '@vkontakte/vkui';

import Icon28DoneOutline from '@vkontakte/icons/dist/28/done_outline';
import Icon28CoinsOutline from '@vkontakte/icons/dist/28/coins_outline';
import Icon28DeleteOutline from '@vkontakte/icons/dist/28/delete_outline';
import Icon28PaletteOutline from '@vkontakte/icons/dist/28/palette_outline';
import Icon28TargetOutline from '@vkontakte/icons/dist/28/target_outline';


export default class Settings extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            api_url: "https://xelene.ru/road/php/index.php?",

        }
        var props = this.props.this;
        this.setPopout = props.setPopout;
        this.showErrorAlert = props.showErrorAlert;
        this.showAlert = props.showAlert;
        this.setActiveModal = props.setActiveModal;
        this.onChange = (event) => {
            var name = event.currentTarget.name;
            var value = event.currentTarget.value;
            this.setState({ [name]: value });
        }
    }

    render() {
        var props = this.props.this;
        return(
            <Panel id={this.props.id}>
                <PanelHeader 
                    left={
                        <PanelHeaderBack onClick={() => this.props.this.goBack()} /> 
                }>
                    Настройки
                </PanelHeader>
                <Group>
                  <SimpleCell
                  before={<Icon28TargetOutline/>}>Ваш возраст</SimpleCell>

                </Group>
                <Group>
                  <SimpleCell
                  before={<Icon28PaletteOutline />}>Смена темы</SimpleCell>
                  <SimpleCell
                  before={<Icon28DoneOutline />}>Верификация</SimpleCell>
                </Group>
                <Group>
                  <SimpleCell
                  before={<Icon28CoinsOutline />}>Баланс</SimpleCell>
                </Group>
                <Group>
                  <CellButton 
                  mode="danger"
                  onClick={<Icon28DeleteOutline />}>Удалить аккаунт</CellButton>
                </Group>
            </Panel>
        )
    }
}
